body.script:
// 
$(document).ready(function() {
  $('body').append('<div class="button-up" style="display: none; opacity: 0.7; width: 50px; height:100%; position: fixed; left: 0; top: 0; cursor: pointer; text-align: center; line-height: 100px; color: #333; font-weight: bold; font-size: 3em;">↑</div>');
  $ (window).scroll (function () {
    if ($ (this).scrollTop () > 300) {
      $ ('.button-up').fadeIn();
    } else {
      $ ('.button-up').fadeOut();
    }
  });
  $('.button-up').click(function(){
    $('body,html').animate({
      scrollTop: 0
    }, 100);
    return false;
  });
  $('.button-up').hover(function() {
    $(this).animate({
      'opacity':'1',
    }).css({'background-color':'#f5f5f5','color':'#555'});
  }, function(){
    $(this).animate({
      'opacity':'0.7'
    }).css({'background':'none','color':'#555'});;
  });
});
//
