  document.addEventListener("DOMContentLoaded", function(event) {
    function getSearchWidth() {
      return (document.body.clientWidth - document.getElementById('Blog1').offsetWidth - 64) / 2;
    }
    var SearchWidth = 'width: ' + getSearchWidth() + 'px';
    var SearchDisplay = 'display: none;';
    var block = document.getElementById('BlogSearch1');
    if(document.body.clientWidth > 1200) {
      block.setAttribute( 'style', SearchWidth );
    } else {
      block.setAttribute( 'style', SearchDisplay );
    }
  });
//

