b:includable.script:
//
function getTopOffset(e) { 
  var y = 0;
  do { y += e.offsetTop; } while (e = e.offsetParent);
  return y;
}
function getWidth() {
  return (document.body.clientWidth - document.getElementById('Blog1').offsetWidth - 64) / 2;
}
var block = document.getElementById('FeaturedPost1');
var PostWidth = 'width: ' + getWidth() + 'px';
var PostDisplay = 'display: none;';
if(document.body.clientWidth > 1200) {
  block.setAttribute( 'style', PostWidth );
} else {
  block.setAttribute( 'style', PostDisplay );
}
if ( null != block ) {
  var topPos = getTopOffset( block );
  window.onscroll = function() {
    var newcss = (topPos < window.pageYOffset) ? 'top: 16px; position: fixed;' : '';
    var PostWidth = 'width: ' + getWidth() + 'px';
    if(document.body.clientWidth > 1200) {
      block.setAttribute( 'style', newcss + '' + PostWidth);
    } else {
      block.setAttribute( 'style', PostDisplay)
    }
  }
}
